import React, { useContext, useState, useEffect } from 'react';
import './View.css';
import { FirebaseContext} from '../../store/Context';
import { getFirestore, collection, query, where, getDocs } from 'firebase/firestore'
import { PostContext } from '../../store/postContext';


function View() {


  const [UserDetails, setUserDetails] = useState()
  const { firebaseApp } = useContext(FirebaseContext);
  const { PostDetails } = useContext(PostContext)
  const firestore = getFirestore(firebaseApp);


  useEffect(async () => {
    
  console.log(PostDetails);
    //  const {userId} = useContext(PostDetails)
    const q = query(collection(firestore, "user"), where("id", "==", 'orQuStsW2oOVaCz9M2flt0UH7A82'));
    const querySnapshot = await getDocs(q);
    const userDetailsData = [];
    querySnapshot.forEach((doc) => {
      userDetailsData.push(doc.data());
      setUserDetails(userDetailsData);
    });
  }, [firestore])

  return (
    <div className="viewParentDiv">
      <div className="imageShowDiv">
        <img
          src="../../../Images/R15V3.jpg" alt="" />
      </div>
      <div className="rightSection" >
        <div className="productDetails">
          <p>&#x20B9; {PostDetails.Price} </p>
          <span>{PostDetails.Name}</span>
          <p>{PostDetails.Category}</p>
          <span>{PostDetails.createdAt}</span>
        </div>

        {UserDetails && <div className="contactDetails">
          <p>Seller details</p>
          <p className='d'>{UserDetails[0].username}</p>
          <p>{UserDetails[0].phone}</p>
        </div>}

      </div>
    </div>
  );
}
export default View;
