import React, { useContext, useState, useEffect } from 'react';
import { FirebaseContext } from '../../store/Context'
import Heart from '../../assets/Heart';
import './Post.css';
import { getFirestore, getDocs, collection } from 'firebase/firestore'

function Posts() {
  const { firebaseApp } = useContext(FirebaseContext)
  const [Products, setProducts] = useState([])
  const fireStore = getFirestore(firebaseApp)

  useEffect(() => {
    const fetchData = async () => {

      const querySnapshot = await getDocs(collection(fireStore, 'products'));

      const productData = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        data: doc.data()
      }));
      console.log(productData);
      setProducts(productData);
    };

    fetchData();
  }, []);

  const handleView=()=>{
    
  }

  return (
    <div className="postParentDiv">
      <div className="moreView">
        <div className="heading">
          <span>Quick Menu</span>
          <span>View more</span>
        </div>
        <div className="cards">

          {Products.map((product) =>
            <div className="card">
              <div className="favorite">
                <Heart></Heart>
              </div>
              <div className="image">
                <img src={product.data.url} alt="" />
              </div>
              <div className="content">
                <p className="rate">&#x20B9; {product.data.Price}</p>
                <span className="kilometer">{product.data.Category}</span>
                <p className="name">{product.data.Name}</p>
              </div>
              <div className="date">
                <span>{product.data.createdAt} </span>
              </div>
            </div>
          )}

        </div>
      </div>
      <div className="recommendations">
        <div className="heading">
          <span>Fresh recommendations</span>
        </div>
        <div className="cardsRecomend" onClick={handleView}>

          <div className="cardRecomend">
            <div className="favorite">
              <Heart></Heart>
            </div>
            <div className="image">
              <img src="../../../Images/R15V3.jpg" alt="" />
            </div>
            <div className="content">
              <p className="rate">&#x20B9; 250000</p>
              <span className="kilometer">Two Wheeler</span>
              <p className="name"> YAMAHA R15V3</p>
            </div>
            <div className="date">
              <span>Tue oct 20 2023</span>
            </div>
          </div>

        </div>
      </div>
    </div>
  );
}

export default Posts;
